/ªi<?php exit; ?>a:2:{s:7:"content";O:8:"stdClass":24:{s:2:"ID";i:8454;s:11:"post_author";s:1:"1";s:9:"post_date";s:19:"2025-11-17 12:34:19";s:13:"post_date_gmt";s:19:"2025-11-17 11:34:19";s:12:"post_content";s:3127:"/* Contenedor central */
.anima-course-container{
  max-width: 1180px;
  margin-inline: auto;
  padding: clamp(16px, 3vw, 28px);
}

/* HERO */
.anima-course-hero .hero-card{
  position: relative;
  border-radius: 22px;
  overflow: hidden;
  min-height: 280px;
  background: #0e1421 center/cover no-repeat;
  border: 1px solid rgba(255,255,255,.08);
  box-shadow: 0 10px 40px rgba(0,0,0,.35);
}
.anima-course-hero .hero-overlay{
  position:absolute; inset:0;
  background: radial-gradient(800px 400px at 70% 20%, rgba(0,180,255,.14), transparent 70%),
              rgba(6,10,18,.35);
  backdrop-filter: blur(2px);
}
.anima-course-hero .hero-body{
  position: relative; z-index: 1;
  padding: clamp(18px, 2.8vw, 28px);
}
.anima-course-hero .badge{
  display:inline-block; font-weight:800;
  font-size:12px; letter-spacing:.08em;
  color:#e9f2ff; opacity:.85; margin-bottom:8px;
}
.anima-course-hero .hero-title{
  margin: 0 0 6px; color:#fff;
  font-size: clamp(26px, 3vw, 42px);
}
.anima-course-hero .chips{ display:flex; gap:8px; flex-wrap:wrap; margin-top:6px }
.anima-course-hero .chip{
  background: rgba(255,255,255,.08);
  border:1px solid rgba(255,255,255,.12);
  color:#d7e6ff; padding:6px 10px; border-radius:999px; font-size:12px
}
.anima-course-hero .hero-metrics{ display:flex; gap:8px; margin-top:10px }
.anima-course-hero .bubble{
  background: linear-gradient(90deg,#2dd4bf,#60a5fa);
  color:#05121a; border-radius:999px; padding:6px 10px; font-weight:800; font-size:12px
}

/* Intro/ficha */
.anima-course-intro{ margin-top: 22px }
.anima-course-meta .meta-card{
  background: rgba(255,255,255,.04);
  border: 1px solid rgba(255,255,255,.08);
  border-radius: 18px;
  margin-top: 16px; overflow:hidden;
}
.anima-course-meta .meta-grid{
  display:grid; gap:18px; padding:18px;
  grid-template-columns: 1fr 1fr;
}
@media (max-width: 900px){
  .anima-course-meta .meta-grid{ grid-template-columns: 1fr; }
}
.meta-table{ width:100%; border-collapse: collapse; }
.meta-table th, .meta-table td{
  text-align:left; padding:8px 0; border-bottom:1px solid rgba(255,255,255,.06);
  color:#cfe1ff;
}
.meta-table th{ opacity:.8; width:42% }
.meta-cta .button{
  display:inline-block; margin-top:12px;
  background: linear-gradient(90deg,#6f65ff,#24d1ff);
  color:#06111b; font-weight:800; padding:.6rem 1.1rem; border-radius:999px; text-decoration:none
}

/* AcordeÃ³n (usa el que ya pinta el plugin) */
.anima-course-syllabus{ margin-top: 12px }

/* CTA 'flotante' limitada al contenedor */
.anima-course-sticky-cta{ display:flex; justify-content:flex-end }
.sticky-cta{
  position: sticky;
  bottom: 16px;
  align-self: flex-end;
  display: inline-flex; gap:10px; align-items:center;
  background: rgba(14,20,33,.75);
  border:1px solid rgba(255,255,255,.08);
  backdrop-filter: blur(6px);
  padding: 10px 12px; border-radius: 999px;
  box-shadow: 0 10px 30px rgba(0,0,0,.35);
}
.sticky-cta .price{
  font-weight: 800; color:#d7e6ff; opacity:.9
}
.sticky-cta .button{
  background: linear-gradient(90deg,#6f65ff,#24d1ff);
  color:#06111b; font-weight:800; padding:.5rem 1rem; border-radius:999px; text-decoration:none
}
